  /**
   * Fallback method to get standard auto-assign suggestions if RnD-specific fails
   */
  private getStandardAutoAssignSuggestions(allDevNewTasks: WorkItem[], normalizedPath: string): void {
    console.log('Getting standard auto-assign suggestions with path:', normalizedPath);
    console.log('Dev-New tasks available for assignment:', allDevNewTasks);

    // Step 1: Get team members with their current task counts
    this.loading.taskCounts = true;

    // Prepare to hold our different data sources
    let developerExpertise: any = {};
    let availableDevelopers: string[] = [];
    let teamMemberTaskCounts: Record<string, number> = {};

    // First, get the team members with their task counts
    this.taskService.getTeamMemberTaskCounts(normalizedPath).pipe(
      tap(taskCounts => {
        teamMemberTaskCounts = taskCounts;
        console.log('Team member task counts:', teamMemberTaskCounts);
      }),
      // Then get developer expertise data from past 3 months
      switchMap(() => {
        // Apply team filter if needed
        let teamMembers = Object.keys(teamMemberTaskCounts);

        // If team filter is active, filter by team members
        if (this.applyTeamFilter && this.filteredTeamMembers.length > 0) {
          const filteredNames = this.filteredTeamMembers.map(m => m.displayName.toLowerCase());
          teamMembers = teamMembers.filter(member => 
            filteredNames.includes(member.toLowerCase())
          );
          console.log(`Applied team filter, filtered to ${teamMembers.length} team members`);
        }

        return this.taskService.getDeveloperExpertise(teamMembers);
      }),
      // Then get developers who have recently completed tasks
      switchMap(expertise => {
        developerExpertise = expertise;
        console.log('Developer expertise:', developerExpertise);
        return this.taskService.getAvailableDevelopers(normalizedPath);
      })
    ).subscribe({
      next: (available) => {
        availableDevelopers = available;
        console.log('Available developers:', availableDevelopers);

        // If team filter is active, filter available developers
        if (this.applyTeamFilter && this.filteredTeamMembers.length > 0) {
          const filteredNames = this.filteredTeamMembers.map(m => m.displayName.toLowerCase());
          availableDevelopers = availableDevelopers.filter(dev => 
            filteredNames.includes(dev.toLowerCase())
          );
          console.log(`Applied team filter to available developers, filtered to ${availableDevelopers.length} developers`);
        }

        // Now we have all 3 data sources:
        // 1. teamMemberTaskCounts - Current workload for each team member
        // 2. developerExpertise - Expertise data from past 3 months
        // 3. availableDevelopers - Developers who have recently completed tasks

        this.assignPreviewSuggestions = {};

        // For each task, find the best developer to assign it to
        allDevNewTasks.forEach(task => {
          let assignedDeveloper = '';
          let assignmentReason = '';

          // Check if there's a developer with expertise for this type of task
          if (developerExpertise && developerExpertise.taskTypeExpertise) {
            const taskType = task.type || 'Unknown';
            const experts = developerExpertise.taskTypeExpertise[taskType];

            if (experts && experts.length > 0) {
              // Filter experts to match team filter if active
              let filteredExperts = [...experts];
              if (this.applyTeamFilter && this.filteredTeamMembers.length > 0) {
                const filteredNames = this.filteredTeamMembers.map(m => m.displayName.toLowerCase());
                filteredExperts = filteredExperts.filter(expert => 
                  filteredNames.includes(expert.toLowerCase())
                );
                console.log(`Applied team filter to ${taskType} experts, filtered from ${experts.length} to ${filteredExperts.length}`);
              }

              // If we still have experts after filtering
              if (filteredExperts.length > 0) {
                // Find the expert with the lowest current workload
                const expertWithLeastTasks = filteredExperts.sort((a: string, b: string) => 
                  (teamMemberTaskCounts[a] || 0) - (teamMemberTaskCounts[b] || 0)
                )[0];

                if (expertWithLeastTasks) {
                  assignedDeveloper = expertWithLeastTasks;
                  assignmentReason = `past expertise in ${taskType} tasks`;
                }
              }
            }
          }

          // If no expert found, check if there are developers who have recently completed tasks
          if (!assignedDeveloper && availableDevelopers.length > 0) {
            // Find the available developer with the lowest workload
            const availableWithLeastTasks = availableDevelopers.sort((a, b) => 
              (teamMemberTaskCounts[a] || 0) - (teamMemberTaskCounts[b] || 0)
            )[0];

            if (availableWithLeastTasks) {
              assignedDeveloper = availableWithLeastTasks;
              assignmentReason = 'recently completed other tasks';
            }
          }

          // If still no assignment, use the team member with the lowest current task count
          if (!assignedDeveloper) {
            // Get team members respecting the team filter
            let teamMembers = Object.keys(teamMemberTaskCounts);

            if (this.applyTeamFilter && this.filteredTeamMembers.length > 0) {
              const filteredNames = this.filteredTeamMembers.map(m => m.displayName.toLowerCase());
              teamMembers = teamMembers.filter(member => 
                filteredNames.includes(member.toLowerCase())
              );
              console.log(`Applied team filter to workload check, filtered to ${teamMembers.length} team members`);
            }

            if (teamMembers.length > 0) {
              const memberWithLeastTasks = teamMembers.sort((a, b) => 
                (teamMemberTaskCounts[a] || 0) - (teamMemberTaskCounts[b] || 0)
              )[0];

              if (memberWithLeastTasks) {
                assignedDeveloper = memberWithLeastTasks;
                assignmentReason = 'lowest current workload';
              }
            }
          }

          // Final fallback: If all else fails, just assign to any team member
          if (!assignedDeveloper) {
            // Try to get from filtered team members based on team filter setting
            let eligibleMembers = this.applyTeamFilter ? this.filteredTeamMembers : this.teamMembers;

            if (eligibleMembers.length > 0) {
              // Use the first team member as a fallback
              const fallbackMember = eligibleMembers[0];
              assignedDeveloper = fallbackMember.displayName;
              assignmentReason = 'default assignment (no criteria met)';

              console.log(`Using fallback assignment for task ${task.id} to ${assignedDeveloper}`);
            } else if (this.teamMembers.length > 0) {
              // If no filtered members, try using any available team member
              const fallbackMember = this.teamMembers[0];
              assignedDeveloper = fallbackMember.displayName;
              assignmentReason = 'default assignment (no criteria met)';

              console.log(`Using fallback assignment for task ${task.id} to ${assignedDeveloper}`);
            } else {
              // Absolute last resort - use a hardcoded name
              assignedDeveloper = 'Unassigned Task';
              assignmentReason = 'could not determine assignee';
              console.warn(`No team members available to assign task ${task.id}`);
            }
          }

          // Store the suggestion with the reason
          if (assignedDeveloper) {
            this.assignPreviewSuggestions[task.id] = `${assignedDeveloper} (${assignmentReason})`;
          }
        });

        // Filter tasks to only include those we have suggestions for
        this.assignPreviewTasks = allDevNewTasks.filter(task => 
          this.assignPreviewSuggestions[task.id]
        );

        console.log('Intelligent auto-assign suggestions:', this.assignPreviewSuggestions);
        console.log('Tasks to be assigned:', this.assignPreviewTasks);

        // Check if we have valid suggestions
        if (this.assignPreviewTasks.length === 0) {
          this.error.preview = 'No suitable tasks found for assignment. Check if there are unassigned Dev-New tasks.';
        }

        this.loading.preview = false;
        this.loading.taskCounts = false;
      },
      error: (err) => {
        console.error('Error loading data for intelligent assignment:', err);

        // Fallback to basic assignment if data loading fails
        this.fallbackToBasicAssignment(allDevNewTasks);
      }
    });
  }

  /**
   * Basic fallback assignment method if intelligent assignment fails
   */
  private fallbackToBasicAssignment(allDevNewTasks: WorkItem[]): void {
    console.log('Falling back to basic assignment logic');

    // Always ensure we have tasks to assign
    if (allDevNewTasks.length === 0) {
      this.error.preview = 'No Dev-New tasks found for assignment.';
      this.loading.preview = false;
      return;
    }

    this.taskService.getAutoAssignSuggestions(this.currentIterationPath).subscribe({
      next: (suggestions) => {
        console.log('Got standard suggestions (basic fallback):', suggestions);

        // Check if we have any suggestions from the API
        if (Object.keys(suggestions).length > 0) {
          this.assignPreviewSuggestions = suggestions;

          // Filter tasks to only include those in the suggestions
          const suggestedTaskIds = Object.keys(suggestions).map(id => parseInt(id));
          this.assignPreviewTasks = allDevNewTasks.filter(task => 
            suggestedTaskIds.includes(task.id)
          );
        }

        // If we still don't have tasks to assign, use ALL Dev-New tasks
        if (this.assignPreviewTasks.length === 0) {
          console.log('No task suggestions matched. Using all Dev-New tasks for assignment.');
          // Include all Dev-New tasks, prioritizing unassigned ones
          this.assignPreviewTasks = [...allDevNewTasks];

          // Create simple suggestions for all tasks
          if (this.assignPreviewTasks.length > 0) {
            this.assignPreviewSuggestions = {};

            // Get available team members
            let availableMembers = this.filteredTeamMembers
              .map(m => m.displayName)
              .filter(Boolean);

            // If no filtered members, use unfiltered members
            if (availableMembers.length === 0 && this.teamMembers.length > 0) {
              availableMembers = this.teamMembers
                .filter(m => typeof m === 'object')
                .map(m => m.displayName)
                .filter(Boolean);
            }

            // Fallback if still no members
            if (availableMembers.length === 0) {
              availableMembers = ['Default Assignee'];
            }

            // Assign members to tasks in a round-robin fashion
            this.assignPreviewTasks.forEach((task, index) => {
              const memberIndex = index % availableMembers.length;
              const member = availableMembers[memberIndex];
              this.assignPreviewSuggestions[task.id] = `${member} (basic fallback assignment)`;
            });
          }
        }

        // Final validation
        if (this.assignPreviewTasks.length === 0) {
          this.error.preview = 'No tasks available for assignment. Check if there are Dev-New tasks in the current iteration.';
        }

        this.loading.preview = false;
      },
      error: (err) => {
        // If API call fails, still try to assign tasks based on team members
        console.error('Failed to load auto-assign suggestions:', err);

        // Use all Dev-New tasks
        this.assignPreviewTasks = [...allDevNewTasks];
        this.assignPreviewSuggestions = {};

        // Get team members to use for assignment
        let availableMembers = this.filteredTeamMembers
          .map(m => m.displayName)
          .filter(Boolean);

        if (availableMembers.length === 0 && this.teamMembers.length > 0) {
          availableMembers = this.teamMembers
            .filter(m => typeof m === 'object')
            .map(m => m.displayName)
            .filter(Boolean);
        }

        if (availableMembers.length === 0) {
          availableMembers = ['Default Assignee'];
        }

        // Assign all tasks
        this.assignPreviewTasks.forEach((task, index) => {
          const memberIndex = index % availableMembers.length;
          const member = availableMembers[memberIndex];
          this.assignPreviewSuggestions[task.id] = `${member} (error recovery assignment)`;
        });

        this.loading.preview = false;
      }
    });
  }

