import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { EstimationService, TaskEstimationResponse } from '../../services/estimation.service';
import { TaskService } from '../../services/task.service';
import { WorkItem, TeamMember } from '../../models/task.model';

@Component({
  selector: 'app-task-estimation',
  templateUrl: './task-estimation.component.html',
  styleUrls: ['./task-estimation.component.scss']
})
export class TaskEstimationComponent implements OnInit {
  estimationForm: FormGroup;
  teamMembers: string[] = [];
  taskTypes: string[] = [
    'Bug',
    'Change Request',
    'Feature',
    'Documentation',
    'Test',
    'Research'
  ];
  complexityLevels: string[] = [
    'Low',
    'Medium',
    'High'
  ];
  
  loading = false;
  error: string | null = null;
  estimationResult: TaskEstimationResponse | null = null;
  showResult = false;

  // New properties for iteration path functionality
  availableIterationPaths: string[] = [];
  selectedIterationPath: string = '';
  workItems: WorkItem[] = [];
  loadingIterationItems: boolean = false;
  iterationError: string | null = null;
  selectedWorkItem: WorkItem | null = null;
  devTimeEstimate: number = 0;
  testTimeEstimate: number = 0;
  totalEstimate: number = 0;

  constructor(
    private fb: FormBuilder,
    private estimationService: EstimationService,
    private taskService: TaskService
  ) {
    this.estimationForm = this.fb.group({
      title: ['', [Validators.required, Validators.minLength(5)]],
      type: ['Feature', Validators.required],
      assignee: [''],
      complexity: ['Medium', Validators.required]
    });
  }

  ngOnInit(): void {
    // Load team members
    this.taskService.getTeamMembers().subscribe({
      next: (response) => {
        if (Array.isArray(response)) {
          if (typeof response[0] === 'string') {
            // It's an array of strings
            this.teamMembers = response as string[];
          } else {
            // It's an array of TeamMember objects
            this.teamMembers = (response as TeamMember[]).map(m => m.displayName);
          }
        }
      },
      error: (err) => {
        console.error('Error loading team members:', err);
        this.error = 'Failed to load team members';
      }
    });

    // Load available iteration paths
    this.loadIterationPaths();
  }

  // Load available iteration paths
  loadIterationPaths(): void {
    this.taskService.getIterationPaths().subscribe({
      next: (paths) => {
        this.availableIterationPaths = paths;
      },
      error: (err) => {
        console.error('Error loading iteration paths:', err);
        this.iterationError = 'Failed to load iteration paths';
      }
    });
  }

  // Load work items for the selected iteration path
  loadWorkItemsByIteration(): void {
    if (!this.selectedIterationPath) {
      this.iterationError = 'Please select an iteration path';
      return;
    }

    this.loadingIterationItems = true;
    this.workItems = [];
    this.selectedWorkItem = null;
    this.iterationError = null;

    this.taskService.getTasks(this.selectedIterationPath).subscribe({
      next: (items) => {
        this.workItems = items;
        this.loadingIterationItems = false;
      },
      error: (err) => {
        console.error('Error loading work items:', err);
        this.iterationError = 'Failed to load work items for the selected iteration';
        this.loadingIterationItems = false;
      }
    });
  }

  // Select a work item for estimation
  selectWorkItem(item: WorkItem): void {
    this.selectedWorkItem = item;
    this.populateFormWithWorkItem(item);
  }

  // Populate the form with selected work item details
  populateFormWithWorkItem(item: WorkItem): void {
    const complexity = this.determineComplexity(item);
    
    this.estimationForm.patchValue({
      title: item.title,
      type: item.type || 'Feature',
      assignee: item.assignedTo || '',
      complexity: complexity
    });
  }

  // Determine complexity based on work item properties
  determineComplexity(item: WorkItem): string {
    // Logic to determine complexity based on priority or other factors
    if (item.priority <= 1) {
      return 'High';
    } else if (item.priority === 2) {
      return 'Medium';
    } else {
      return 'Low';
    }
  }

  resetForm(): void {
    this.estimationForm.reset({
      type: 'Feature',
      complexity: 'Medium'
    });
    this.error = null;
    this.showResult = false;
    this.estimationResult = null;
  }

  estimateTaskTime(): void {
    if (this.estimationForm.invalid) {
      // Mark all fields as touched to trigger validation messages
      Object.keys(this.estimationForm.controls).forEach(key => {
        this.estimationForm.get(key)?.markAsTouched();
      });
      return;
    }

    this.loading = true;
    this.error = null;
    this.estimationResult = null;
    this.showResult = false;

    const request = {
      title: this.estimationForm.value.title,
      type: this.estimationForm.value.type,
      assignee: this.estimationForm.value.assignee,
      complexity: this.estimationForm.value.complexity
    };

    this.estimationService.estimateTaskTime(request).subscribe({
      next: (result) => {
        this.estimationResult = result;
        this.showResult = true;
        this.loading = false;
        
        // Calculate dev and test time estimates
        this.calculateTimeEstimates(result.estimatedHours);
      },
      error: (err) => {
        this.error = err.message || 'An error occurred during estimation';
        this.loading = false;
      }
    });
  }

  // Calculate development and testing time estimates
  calculateTimeEstimates(totalHours: number): void {
    // Typical distribution: dev 60-70%, testing 30-40%
    const complexity = this.estimationForm.value.complexity;
    let devRatio = 0.65; // Default dev ratio
    
    // Adjust ratio based on complexity
    if (complexity === 'High') {
      devRatio = 0.7; // Higher complexity = more dev time
    } else if (complexity === 'Low') {
      devRatio = 0.6; // Lower complexity = less dev time
    }
    
    this.devTimeEstimate = Math.round(totalHours * devRatio * 10) / 10;
    this.testTimeEstimate = Math.round((totalHours - this.devTimeEstimate) * 10) / 10;
    this.totalEstimate = totalHours;
  }

  // Format confidence score as percentage
  formatConfidence(score: number): string {
    return `${Math.round(score * 100)}%`;
  }

  // Get appropriate class for confidence level
  getConfidenceClass(score: number): string {
    if (score >= 0.8) {
      return 'bg-success';
    } else if (score >= 0.6) {
      return 'bg-primary';
    } else if (score >= 0.4) {
      return 'bg-warning';
    } else {
      return 'bg-danger';
    }
  }
}
