import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse, HttpParams } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, retry, map } from 'rxjs/operators';
import { environment } from '../../environments/environment';
import { TeamMember } from '../models/task.model';

@Injectable({
  providedIn: 'root'
})
export class TeamService {
  private apiUrl = `${environment.apiUrl}/tasks/team-members`;

  constructor(private http: HttpClient) { }

  /**
   * Fetches all team members from the API
   * @returns Observable of TeamMember array
   */
  getTeamMembers(): Observable<TeamMember[]> {
    console.log(`Calling API endpoint: ${this.apiUrl}`);
    return this.http.get<TeamMember[]>(this.apiUrl)
      .pipe(
        retry(1), // Retry once on failure
        catchError(this.handleError),
        map(response => {
          console.log('Raw API response:', response);
          return this.processTeamMembersResponse(response);
        })
      );
  }

  /**
   * Fetches team members from a specific team
   * @param teamName The name of the team to fetch members from (e.g., "RND")
   * @param iterationPath Optional iteration path to filter by
   * @returns Observable of TeamMember array
   */
  getTeamMembersByTeam(teamName: string, iterationPath?: string): Observable<TeamMember[]> {
    let params = new HttpParams().set('teamName', teamName);
    
    if (iterationPath) {
      params = params.set('iterationPath', iterationPath);
    }
    
    console.log(`Calling team-specific API endpoint: ${this.apiUrl} with params:`, params.toString());
    
    return this.http.get<TeamMember[]>(this.apiUrl, { params })
      .pipe(
        retry(1), // Retry once on failure
        catchError(this.handleError),
        map(response => {
          console.log(`Raw API response for team ${teamName}:`, response);
          return this.processTeamMembersResponse(response);
        })
      );
  }

  /**
   * Handles HTTP errors and returns a user-friendly error message
   * @param error The HTTP error response
   * @returns An observable that errors with a user-friendly message
   */
  private handleError(error: HttpErrorResponse): Observable<never> {
    let errorMessage = 'An unknown error occurred while fetching team members';
    
    if (error.error instanceof ErrorEvent) {
      // Client-side error
      errorMessage = `Error: ${error.error.message}`;
    } else {
      // Server-side error
      errorMessage = `Server returned code ${error.status}: ${error.statusText}`;
      if (error.error?.message) {
        errorMessage += ` - ${error.error.message}`;
      }
    }
    
    console.error(errorMessage);
    return throwError(() => new Error(errorMessage));
  }

  /**
   * Processes the API response to ensure it conforms to the expected structure
   * @param response The raw API response
   * @returns An array of properly formatted TeamMember objects
   */
  private processTeamMembersResponse(response: any): TeamMember[] {
    console.log('Processing raw team members response:', response);
    
    if (!Array.isArray(response)) {
      console.warn('Unexpected response format for team members (not an array):', response);
      return [];
    }
    
    if (response.length === 0) {
      console.warn('Empty response received for team members');
      return [];
    }
    
    // Examine the first item to determine the format
    const firstItem = response[0];
    console.log('First item in response:', firstItem);
    
    // Check if we have the expected format from the Swagger API with these fields
    const isSwaggerFormat = typeof firstItem === 'object' && 
                           'id' in firstItem && 
                           'displayName' in firstItem && 
                           'team' in firstItem;
    
    if (isSwaggerFormat) {
      console.log('Detected Swagger API format with expected fields');
      return response.map((member: any) => ({
        id: member.id,
        displayName: member.displayName,
        uniqueName: member.uniqueName || member.email || '',
        imageUrl: member.imageUrl || undefined,
        currentWorkload: typeof member.currentWorkload === 'number' ? member.currentWorkload : 0,
        isActive: member.isActive !== undefined ? member.isActive : true,
        email: member.email || member.uniqueName || '',
        team: member.team || 'RND Team'
      }));
    }
    
    // Handle case where response is array of strings (just names)
    if (typeof firstItem === 'string') {
      console.log('Processing string array response:', response);
      return response.map((name: string, index: number) => ({
        id: `member-${index}`,
        displayName: name,
        uniqueName: name,
        imageUrl: undefined,
        currentWorkload: 0,
        isActive: true,
        email: '',
        team: 'RND Team' // Assume these are all RND team members
      }));
    }
    
    // Handle case where response is array of partially formatted objects
    console.log('Processing generic object array response');
    return response.map((member: any, index: number) => ({
      id: member.id || `member-${index}`,
      displayName: member.displayName || member.name || `Unknown User ${index}`,
      uniqueName: member.uniqueName || member.email || member.displayName || '',
      imageUrl: member.imageUrl || undefined,
      // Add missing properties with default values
      currentWorkload: typeof member.currentWorkload === 'number' ? member.currentWorkload : 0,
      isActive: member.isActive !== undefined ? member.isActive : true,
      email: member.email || member.uniqueName || '',
      team: member.team || 'RND Team'
    }));
  }
}
